const Detail = () => {
  const { product_id } = useParams()
  const allProducts = mockAllProducts.products;
  const findProduct = allProducts[product_id];

  const [token, setToken] = useState(null);
  const [products, setProducts] = useState([]);
  const [plainTabs, setPlainTabs] = useState(40);
  const [currentImage, setCurrentImage] = useState(findProduct.thumbnail);
  const [isOpen, setIsOpen] = useState(false);
  const [photoIndex, setPhotoIndex] = useState(0);

  const checkToken = async () => {
    const token = await localStorage.getItem("token");
    if (token) {
      setToken(token);
      return true;
    }
    return false;
  };

  const getProducts = async () => {
    const data = await axios.get("https://dummyjson.com/products");
    const products = data.data.products;
    setProducts(products);
    console.log(products);
  };

  const handleClickPreviewImg = () => {
    let index = findProduct.images.findIndex(item => item == currentImage);
    setPhotoIndex(index);
    setIsOpen(true);
  }

  useEffect(() => {
    const fetchData = async () => {
      document.documentElement.scrollTop = 0;
      document.scrollingElement.scrollTop = 0;
      await checkToken();
      await getProducts();
    };
    fetchData();
  }, []);

