/*!

=========================================================
* Argon Design System React - v1.1.1
=========================================================

* Product Page: https://www.creative-tim.com/product/argon-design-system-react
* Copyright 2022 Creative Tim (https://www.creative-tim.com)
* Licensed under MIT (https://github.com/creativetimofficial/argon-design-system-react/blob/master/LICENSE.md)

* Coded by Creative Tim

=========================================================

* The above copyright notice and this permission notice shall be included in all copies or substantial portions of the Software.

*/
import React from "react";
import { Link } from 'react-router-dom';
// nodejs library that concatenates classes
import classnames from "classnames";
import axios from 'axios';

// reactstrap components
import {
  Badge,
  Button,
  Card,
  CardBody,
  CardImg,
  FormGroup,
  Input,
  InputGroupAddon,
  InputGroupText,
  InputGroup,
  Container,
  Row,
  Col,
  NavItem,
  NavLink,
  Nav,
} from "reactstrap";

// core components
import DemoNavbar from "components/Navbars/DemoNavbar.js";
import CardsFooter from "components/Footers/CardsFooter.js";
import Lightbox from 'react-image-lightbox';

// index page sections
import Download from "../IndexSections/Download.js";
const images = [
  "//giayxshop.vn/wp-content/uploads/2022/07/z3657932021630_50a8632c81be92e2861408d560765cae.jpg",
  "//giayxshop.vn/wp-content/uploads/2022/07/z3657932085521_77caa9ac7a21d8a617596b66034716d1.jpg",
  "//giayxshop.vn/wp-content/uploads/2022/07/z3657932051478_b6563a93b7f50c597f77b81a7e8a408e.jpg",
  "//giayxshop.vn/wp-content/uploads/2022/07/z3657932032657_8d9cf937dee67b4cb4c79ecf2f61a82b.jpg",
  "//giayxshop.vn/wp-content/uploads/2022/07/z3657932029111_414990a61d2c8c1d3fb1e0e7e2446d1a.jpg"
]

class Detail extends React.Component {
  state = {

  };

  products = [];

  

  constructor(props) {
    super(props);
    this.state = {
      plainTabs: 1,
      products: this.products,
      currentImage: "https://giayxshop.vn/wp-content/uploads/2022/07/z3657932021630_50a8632c81be92e2861408d560765cae.jpg",
      isOpen: false,
      photoIndex: 0,
    };
  }

  toggleNavs = (e, state, index) => {
    e.preventDefault();
    this.setState({
      [state]: index
    });
    
  };



  async checkToken() {
    const token = await localStorage.getItem('token');
    if (token) {
      this.setState({ ...this.state, token: token });
      return true;
    }
    return false;
  }

  async componentDidMount() {
    document.documentElement.scrollTop = 0;
    document.scrollingElement.scrollTop = 0;
    this.refs.main.scrollTop = 0;
    await this.checkToken();
    await this.getProducts();

  }



  getProducts = async () => {
    const data = await axios.get('https://dummyjson.com/products');
    this.products = data.data.products;
    this.setState({ ...this.state, products: this.products });
    console.log(this.products);
  }



  render() {
    const { photoIndex, isOpen } = this.state;
    return (
      <>
        <DemoNavbar />
        <main ref="main">
          <div className="position-relative">
            {/* shape Hero */}
            <section className="section section-lg section-shaped pb-250">
              <div className="shape shape-style-1 shape-default">
                <span />
                <span />
                <span />
                <span />
                <span />
                <span />
                <span />
                <span />
                <span />
              </div>
              {!this.state.token && (
                <Container className="py-lg-md d-flex">
                  <div className="col px-0">
                    <Row>
                      <Col lg="6">
                        <h1 className="display-3 text-white">
                          A beautiful Design System{" "}
                          <span>completed with examples</span>
                        </h1>
                        <p className="lead text-white">
                          The design system comes with four pre-built pages to
                          help you get started faster. You can change the text and
                          images and you're good to go.
                        </p>
                        <div className="btn-wrapper">
                          <Button
                            className="btn-white btn-icon mb-3 mb-sm-0 ml-1"
                            color="default"
                            href="https://www.creative-tim.com/product/argon-design-system-react?ref=adsr-landing-page"
                          >
                            <span className="btn-inner--icon mr-1">
                              <i className="ni ni-cloud-download-95" />
                            </span>
                            <span className="btn-inner--text">
                              Please sign in to see our product!
                            </span>
                          </Button>
                        </div>
                      </Col>
                    </Row>
                  </div>
                </Container>
              )}
              {/* SVG separator */}
              <div className="separator separator-bottom separator-skew">
                <svg
                  xmlns="http://www.w3.org/2000/svg"
                  preserveAspectRatio="none"
                  version="1.1"
                  viewBox="0 0 2560 100"
                  x="0"
                  y="0"
                >
                  <polygon
                    className="fill-white"
                    points="2560 0 2560 100 0 100"
                  />
                </svg>
              </div>
            </section>
            {/* 1st Hero Variation */}
          </div>
          {this.state.token && (
            <section className="section section-lg pt-lg-0 mt--300">
              <Container>
                <Row className="justify-content-center">
                  <Col lg="12">
                    <h2 className="h2 text-white font-weight-bold mt-md mb-4">
                      Đặt hàng
                    </h2>
                    <Row className="row-grid">
                      {/* {this.state.products && this.state.products.map((item, idx) => (
                        <Col lg="6" >
                          <p>left content</p>
                          <Card className="card-lift--hover shadow border-0 text-center"> 
                            <CardBody className="py-5">
                              <img width={200} height={150} src={item.thumbnail} />
                              <h6 className="text-warning mt-3" >
                                {item.brand} - {item.category}
                              </h6>
                              <h5 className="price mt-3 text-primary">
                                {item.price} $
                              </h5>
                              <Link to="/detail-page">
                                <Button
                                  className="mt-3"
                                  color="warning"
                                  href="#pablo"
                                  onClick={(e) => e.preventDefault()}
                                >
                                  Click to buy
                                </Button>
                              </Link>
                            </CardBody>
                          </Card>
                        </Col>
                        
                      ))} */}
                      <Col lg="7" >
                        <div className="img-up">
                          <img src={this.state.currentImage} style={{ width: "580px", height: "400px" }} onClick={() => this.setState({isOpen: true })} />
                        </div>
                        <div className="img-down" style={{
                          display: "flex",
                          gap: "10px",
                          marginTop: "15px",
                        }}>
                          <div className="img-small" >
                            <img src="https://giayxshop.vn/wp-content/uploads/2022/07/z3657932021630_50a8632c81be92e2861408d560765cae.jpg" style={{ width: "100px", height: "100px" }} onClick={(e) => this.toggleNavs(e, "currentImage", "https://giayxshop.vn/wp-content/uploads/2022/07/z3657932021630_50a8632c81be92e2861408d560765cae.jpg")} />
                          </div>
                          <div className="img-small" >
                            <img src="https://giayxshop.vn/wp-content/uploads/2022/07/z3657932085521_77caa9ac7a21d8a617596b66034716d1.jpg" style={{ width: "100px", height: "100px" }} onClick={(e) => this.toggleNavs(e, "currentImage", "https://giayxshop.vn/wp-content/uploads/2022/07/z3657932085521_77caa9ac7a21d8a617596b66034716d1.jpg")} />
                          </div>
                          <div className="img-small" >
                            <img src="https://giayxshop.vn/wp-content/uploads/2022/07/z3657932051478_b6563a93b7f50c597f77b81a7e8a408e.jpg" style={{ width: "100px", height: "100px" }} onClick={(e) => this.toggleNavs(e, "currentImage", "https://giayxshop.vn/wp-content/uploads/2022/07/z3657932051478_b6563a93b7f50c597f77b81a7e8a408e.jpg")} />
                          </div>
                          <div className="img-small" >
                            <img src="https://giayxshop.vn/wp-content/uploads/2022/07/z3657932032657_8d9cf937dee67b4cb4c79ecf2f61a82b.jpg" style={{ width: "100px", height: "100px" }} onClick={(e) => this.toggleNavs(e, "currentImage", "https://giayxshop.vn/wp-content/uploads/2022/07/z3657932032657_8d9cf937dee67b4cb4c79ecf2f61a82b.jpg")} />
                          </div>
                          <div className="img-small" >
                            <img src="https://giayxshop.vn/wp-content/uploads/2022/07/z3657932029111_414990a61d2c8c1d3fb1e0e7e2446d1a.jpg" style={{ width: "100px", height: "100px" }} onClick={(e) => this.toggleNavs(e, "currentImage", "https://giayxshop.vn/wp-content/uploads/2022/07/z3657932029111_414990a61d2c8c1d3fb1e0e7e2446d1a.jpg")} />
                          </div>
                        </div>

                      </Col>
                      <Col lg="5" >
                        <Card className="shadow border-0 ">
                          <CardBody className="py-2">
                            <h2 className="text-default font-weight-bold mb-3">Oppo F19</h2>
                            <h3 className="text-default font-weight-bold mb-3">$280</h3>
                            <h5 className="text-default font-weight-bold ">
                              Size
                            </h5>
                            <div className="nav-wrapper">
                              <Nav
                                className="nav-fill flex-column flex-md-row"
                                id="tabs-icons-text"
                                pills
                                role="tablist"
                              >
                                <NavItem>
                                  <NavLink
                                    aria-selected={this.state.plainTabs === 1}
                                    className={classnames("mb-sm-3 mb-md-0", {
                                      active: this.state.plainTabs === 1
                                    })}
                                    onClick={(e) => this.toggleNavs(e, "plainTabs", 1)}
                                    href="#pablo"
                                    role="tab"
                                  >
                                    40
                                  </NavLink>
                                </NavItem>
                                <NavItem>
                                  <NavLink
                                    aria-selected={this.state.plainTabs === 2}
                                    className={classnames("mb-sm-3 mb-md-0", {
                                      active: this.state.plainTabs === 2
                                    })}
                                    onClick={(e) => this.toggleNavs(e, "plainTabs", 2)}
                                    href="#pablo"
                                    role="tab"
                                  >
                                    41
                                  </NavLink>
                                </NavItem>
                                <NavItem>
                                  <NavLink
                                    aria-selected={this.state.plainTabs === 3}
                                    className={classnames("mb-sm-3 mb-md-0", {
                                      active: this.state.plainTabs === 3
                                    })}
                                    onClick={(e) => this.toggleNavs(e, "plainTabs", 3)}
                                    href="#pablo"
                                    role="tab"
                                  >
                                    42
                                  </NavLink>
                                </NavItem>
                                <NavItem>
                                  <NavLink
                                    aria-selected={this.state.plainTabs === 4}
                                    className={classnames("mb-sm-3 mb-md-0", {
                                      active: this.state.plainTabs === 4
                                    })}
                                    onClick={(e) => this.toggleNavs(e, "plainTabs", 4)}
                                    href="#pablo"
                                    role="tab"
                                  >
                                    43
                                  </NavLink>
                                </NavItem>
                                <NavItem>
                                  <NavLink
                                    aria-selected={this.state.plainTabs === 5}
                                    className={classnames("mb-sm-3 mb-md-0", {
                                      active: this.state.plainTabs === 5
                                    })}
                                    onClick={(e) => this.toggleNavs(e, "plainTabs", 5)}
                                    href="#pablo"
                                    role="tab"
                                  >
                                    44
                                  </NavLink>
                                </NavItem>
                              </Nav>
                            </div>
                            <Button block color="warning" size="lg" type="button" className="mt-3 mb-3">
                              Mua ngay
                            </Button>
                          </CardBody>
                        </Card>

                      </Col>
                    </Row>
                  </Col>
                </Row>
              </Container>

              {isOpen && (
                <Lightbox
                  mainSrc={images[photoIndex]}
                  nextSrc={images[(photoIndex + 1) % images.length]}
                  prevSrc={images[(photoIndex + images.length - 1) % images.length]}
                  onCloseRequest={() => this.setState({ isOpen: false })}
                  onMovePrevRequest={() =>
                    this.setState({
                      photoIndex: (photoIndex + images.length - 1) % images.length,
                    })
                  }
                  onMoveNextRequest={() =>
                    this.setState({
                      photoIndex: (photoIndex + 1) % images.length,
                    })
                  }
                />
              )}
            </section>
          )}



        </main>
        <CardsFooter />
      </>
    );
  }
}

export default Detail;
